  transition: filter 0.3s;
}

.progressive-image.blur {
  filter: blur(5px);
}

.progressive-image.loaded {
  filter: none;
}
*/
```

## Caching Strategies

### Service Worker Caching

```javascript
// Advanced service worker caching
const CACHE_NAME = 'app-v1';
const STATIC_CACHE = 'static-v1';
const DYNAMIC_CACHE = 'dynamic-v1';

const STATIC_ASSETS = [
  '/',
  '/css/main.css',
  '/js/main.js',
  '/images/logo.png'
];

// Install event
self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(STATIC_CACHE)
      .then(cache => cache.addAll(STATIC_ASSETS))
  );
});

// Fetch event with advanced caching strategies
self.addEventListener('fetch', event => {
  const { request } = event;
  const url = new URL(request.url);

  // Handle different resource types
  if (request.destination === 'image') {
    event.respondWith(handleImageRequest(request));
  } else if (url.pathname.startsWith('/api/')) {
    event.respondWith(handleAPIRequest(request));
  } else {
    event.respondWith(handleStaticRequest(request));
  }
});

// Image caching strategy
async function handleImageRequest(request) {
  const cache = await caches.open(DYNAMIC_CACHE);
  const cachedResponse = await cache.match(request);

  if (cachedResponse) {
    return cachedResponse;
  }

  try {
    const networkResponse = await fetch(request);
    cache.put(request, networkResponse.clone());
    return networkResponse;
  } catch (error) {
    // Return fallback image
    return caches.match('/images/fallback.png');
  }
}

// API caching with TTL
async function handleAPIRequest(request) {
  const cache = await caches.open(DYNAMIC_CACHE);
  const cachedResponse = await cache.match(request);

  // Check if cached response is still valid (5 minutes TTL)
  if (cachedResponse) {
    const cachedTime = new Date(cachedResponse.headers.get('cached-time'));
    const now = new Date();
    const fiveMinutes = 5 * 60 * 1000;

    if (now - cachedTime < fiveMinutes) {
      return cachedResponse;
    }
  }

  try {
    const networkResponse = await fetch(request);
    const responseClone = networkResponse.clone();

    // Add timestamp header
    const response = new Response(await responseClone.blob(), {
      status: networkResponse.status,
      statusText: networkResponse.statusText,
      headers: {
        ...Object.fromEntries(networkResponse.headers.entries()),
        'cached-time': new Date().toISOString()
      }
    });

    cache.put(request, response.clone());
    return response;
  } catch (error) {
    return cachedResponse || new Response('Offline', { status: 503 });
  }
}
```

### HTTP Caching Headers

```javascript
// Express.js caching headers
app.use('/static', express.static('public', {
  maxAge: '1y', // 1 year for static assets
  etag: true,
  lastModified: true
}));

// API response caching
app.get('/api/data', (req, res) => {
  res.set({
    'Cache-Control': 'public, max-age=300', // 5 minutes
    'ETag': generateETag(data),
    'Last-Modified': data.lastModified
  });

  res.json(data);
});

// Generate ETag
function generateETag(data) {
  return require('crypto')
    .createHash('md5')
    .update(JSON.stringify(data))
    .digest('hex');
}
```

## Memory and CPU Optimization

### Efficient Event Handling

```javascript
// Debounced scroll handler
function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

// Throttled scroll handler
function throttle(func, limit) {
  let inThrottle;
  return function() {
    const args = arguments;
    const context = this;
    if (!inThrottle) {
      func.apply(context, args);
      inThrottle = true;
      setTimeout(() => inThrottle = false, limit);
    }
  };
}

// Usage
const debouncedResize = debounce(() => {
  console.log('Window resized');
}, 300);

const throttledScroll = throttle(() => {
  console.log('Scrolling');
}, 100);

window.addEventListener('resize', debouncedResize);
window.addEventListener('scroll', throttledScroll);
```

### Memory Leak Prevention

```javascript
// Proper cleanup in React components
import { useEffect, useRef } from 'react';

function MyComponent() {
  const intervalRef = useRef();
  const observerRef = useRef();

  useEffect(() => {
    // Set up interval
    intervalRef.current = setInterval(() => {
      console.log('Interval tick');
    }, 1000);

    // Set up observer
    observerRef.current = new IntersectionObserver((entries) => {
      // Handle intersection
    });

    // Cleanup function
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }

      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, []);

  return <div>My Component</div>;
}
```

## Performance Monitoring

### Real User Monitoring (RUM)

```javascript
// Custom performance monitoring
class PerformanceMonitor {
  constructor() {
    this.metrics = {};
    this.init();
  }

  init() {
    this.measurePageLoad();
    this.measureUserInteractions();
    this.measureResourceLoading();
  }

  measurePageLoad() {
    window.addEventListener('load', () => {
      const navigation = performance.getEntriesByType('navigation')[0];

      this.metrics.pageLoad = {
        domContentLoaded: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
        loadComplete: navigation.loadEventEnd - navigation.loadEventStart,
        totalTime: navigation.loadEventEnd - navigation.fetchStart
      };

      this.sendMetrics('pageLoad', this.metrics.pageLoad);
    });
  }

  measureUserInteractions() {
    ['click', 'keydown', 'touchstart'].forEach(eventType => {
      document.addEventListener(eventType, (event) => {
        const startTime = performance.now();

        requestAnimationFrame(() => {
          const endTime = performance.now();
          const interactionTime = endTime - startTime;

          if (interactionTime > 100) {
            this.sendMetrics('slowInteraction', {
              type: eventType,
              duration: interactionTime,
              target: event.target.tagName
            });
          }
        });
      });
    });
  }

  measureResourceLoading() {
    new PerformanceObserver((list) => {
      for (const entry of list.getEntries()) {
        if (entry.duration > 1000) {
          this.sendMetrics('slowResource', {
            name: entry.name,
            duration: entry.duration,
            type: entry.initiatorType
          });
        }
      }
    }).observe({ entryTypes: ['resource'] });
  }

  sendMetrics(type, data) {
    // Send to analytics service
    fetch('/api/metrics', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        type,
        data,
        timestamp: Date.now(),
        userAgent: navigator.userAgent,
        url: window.location.href
      })
    }).catch(console.error);
  }
}

// Initialize monitoring
new PerformanceMonitor();
```

